import React from 'react';

class APIReander extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      loading: false,
      data: ''

    };
  }

  componentDidMount(){
    return fetch('https://wfc2-image-api-259809.appspot.com/api/series/eE6BFpfm/')
      .then((response) => response.json())
      .then((responseJson) => {
        console.log(responseJson);
        this.setState({
          loading: true,
          data: responseJson,
      });
      })
      .catch((error) =>{
        console.error(error);
      });
  }

  render() {
    if(this.state.loading){
      return(
        <div className="App-header">
          <p>{ this.state.data.title }</p>
          <p>{ this.state.data.author }</p>
          <p>{ this.state.data.publisher }</p>
          <p>{ this.state.data.volumes }</p>
          <p>{ this.state.data.description }</p>
          <img src={ this.state.data.seriesImage }/>
          <p>{ this.state.data.books[1] }</p>
        </div>
      );
    }else{
      return(
        <div className="App-header">
          <p>Loading...</p>
        </div>
      );
    }
  }
}


export default APIReander;
