import React from 'react';

class SERIES_ALL extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      loading: false,
      data: ''

    };
  }

  componentDidMount(){
    return fetch('https://wfc2-image-api-259809.appspot.com/api/series/')
      .then((response) => response.json())
      .then((responseJson) => {
        console.log(responseJson);
        this.setState({
          loading: true,
          data: responseJson,
      });
      })
      .catch((error) =>{
        console.error(error);
      });
  }

  render() {
    if(this.state.loading){
      return(
        <div className="App-header">
          <p>{ this.state.data.data[0].title }</p>
          <img src={ this.state.data.data[0].seriesImage } />
          {this.state.data.data.map((Item) => {
              return (
                <div className="series_all_container">
                  <p>{Item.title}</p>
                  <img src={ Item.seriesImage } />
                </div>
              );
            })}
        </div>
      );
    }else{
      return(
        <div className="App-header">
          <p>Loading...</p>
        </div>
      );
    }
  }
}


export default SERIES_ALL;
